import { useEffect, useState } from "react";
import { View, Text, StyleSheet, TextInput, TouchableOpacity, Keyboard } from "react-native";



export default function LoginScreen() {
    const [isPasswordShown, setIsPasswordShown] = useState(true);
    const [button, setButton] = useState(false);
    const [keyboardHeight, setKeyboardHeight] = useState(0);
    useEffect(() => {
        const keyboardDidShowListener = Keyboard.addListener(
            'keyboardDidShow',
            ({ endCoordinates }) => {
                setKeyboardHeight(endCoordinates.height);
            },
        );

        const keyboardDidHideListener = Keyboard.addListener(
            'keyboardDidHide',
            () => {
                setKeyboardHeight(0);
            },
        );

        return () => {
            keyboardDidShowListener.remove();
            keyboardDidHideListener.remove();
        };
    }, []);
    const passwordToggle = () => {
        setIsPasswordShown((value) => !value);
    };

    const onFocusInput = () => {
        setButton(true);
    }
    const onBlurInput = () => {
        setButton(false);
    }
    return <View style={styles.form}>
        <Text style={styles.loginText}>Войти</Text>
        <View style={styles.inputContainer}>
            <TextInput style={{ ...styles.input, marginBottom: keyboardHeight }}
                placeholder="Адрес электронной почты"
                onFocus={onFocusInput}
                onBlur={onBlurInput}
            ></TextInput>
            <View>
                <TextInput style={styles.input}
                    placeholder="Пароль"
                    onFocus={onFocusInput}
                    onBlur={onBlurInput}
                ></TextInput>
                <TouchableOpacity activeOpacity={0.5} style={styles.showBtn} onPress={passwordToggle} >
                    <Text style={styles.showTitle}>{isPasswordShown ? 'Показать' : 'Скрыть'}</Text>
                </TouchableOpacity>
            </View>
        </View>
        {!button && <View>
            <TouchableOpacity activeOpacity={0.8} style={styles.btn}>
                <Text style={styles.btnTitle}>Войти</Text>
            </TouchableOpacity>
            <TouchableOpacity activeOpacity={0.8} style={{ marginTop: 16 }} >
                <Text style={styles.link}>Нет аккаунта? Зарегистрироваться</Text>
            </TouchableOpacity>
        </View>}
    </View >
}


const styles = StyleSheet.create({
    loginText: {
        fontWeight: '500',
        fontSize: 30,
        lineHeight: 35,
        textAlign: 'center',
        color: '#212121',
    },
    form: {
        backgroundColor: "#fff",
        height: '60%',
        width: '100%',
        justifyContent: 'flex-start',
        alignItems: 'center',
        borderTopLeftRadius: 25,
        borderTopRightRadius: 25,
        paddingHorizontal: 16,
        paddingTop: 32
    },
    inputContainer: {
        marginHorizontal: 16,
        marginTop: 16,
    },
    input: {
        height: 50,
        marginTop: 16,
        borderWidth: 1,
        borderColor: "#E8E8E8",
        borderRadius: 8,
        padding: 16,
        backgroundColor: "#F6F6F6",
        width: 343,

    },
    showBtn: {
        position: 'absolute',
        alignSelf: 'flex-end',
        right: 16,
        top: 30
    },
    showTitle: {
        color: '#1B4371',
        fontSize: 16,
        fontWeight: '400',
        lineHeight: 19
    },
    btn: {
        backgroundColor: "#FF6C00",
        borderRadius: 100,
        height: 51,
        alignItems: "center",
        justifyContent: "center",
        marginTop: 43,
        width: 343
    },
    btnTitle: {
        fontSize: 16,
        lineHeight: 19,
        color: "#FFFFFF",
    },
    link: {
        fontSize: 16,
        lineHeight: 19,
        textAlign: "center",
        color: "#1B4371",
    },
})
