import React, { useState } from 'react';
import { StyleSheet, TouchableOpacity, Text, View, ImageBackground, TextInput, TextInputTextInputEventData, KeyboardAvoidingView, Platform } from 'react-native';

const LoginScreen: React.FC = () => {
    const [form, setForm] = useState<IForm>({ email: '', password: '' });
    const [btn, setBtn] = useState(false);
    interface IForm {
        email: string,
        password: string,
    }

    return (
        <View style={styles.form}>
            <KeyboardAvoidingView
                behavior={Platform.OS === 'ios' ? 'padding' : 'height'}
            >
                <Text style={styles.formTtl}>Войти</Text>
                <TextInput style={styles.input} onFocus={() => setBtn(true)} />
                <TextInput style={styles.input} onFocus={() => setBtn(true)} />
                {!btn && <View>
                    <TouchableOpacity style={styles.btn}>
                        <Text style={styles.btnTtl}>Войти</Text>
                    </TouchableOpacity></View>}
                <Text style={styles.textTtl}>Нет аккаунта? Зарегистрироваться</Text>
            </KeyboardAvoidingView>

        </View>
    );
};
const styles = StyleSheet.create({
    text: {
        color: 'white'
    },
    formTtl: {
        fontSize: 30,
        lineHeight: 35,
        textAlign: 'center',
        letteSpacing: '0.01em',
        marginBottom: 33,
        color: '#212121',
    },

    input: {
        borderWidth: 1,
        backgroundColor: '#F6F6F6',
        height: 40,
        borderRadius: 8,
        marginHorizontal: 32,
        color: '#BDBDBD',
        textAlign: 'center',
        marginBottom: 16,
        FontSize: 16,
        LineHeight: 19,
    },
    form: {
        height: 489,
        paddingLeft: 16,
        paddingRight: 16,
        paddingTop: 32,
        margin: 'auto',
        marginTop: 323,
        backgroundColor: '#fff'
    },
    link: {},
    btn: {
        height: 40,
        backgroundColor: '#FF6C00',
        marginHorizontal: 32,
        borderRadius: 100,
        marginTop: 43,
        justifyContent: 'center',
        alignItems: 'center',
    },
    btnTtl: {
        fontSize: 16,
        lineHeight: 19,
        textAlign: 'center',
        color: '#FFFFFF',
    },
    textTtl: {
        fontSize: 16,
        lineHeight: 19,
        textAlign: 'center',
        marginTop: 16,
        color: '#1B4371',
    },


});

export default LoginScreen;
