import { View, Text, StyleSheet, TextInput, TouchableOpacity } from "react-native";



export default function LoginScreen() {
    return <View style={styles.form}>
        <Text style={styles.loginText}>Войти</Text>
        <View style={styles.inputContainer}>
            <TextInput style={styles.input}
                placeholder="Адрес электронной почты"
            ></TextInput>
            <View>
                <TextInput style={styles.input}
                    placeholder="Пароль"
                ></TextInput>
            </View>
            <TouchableOpacity>
                <Text>Войти</Text>
            </TouchableOpacity>
        </View>
    </View>
}


const styles = StyleSheet.create({
    loginText: {
        fontWeight: '500',
        fontSize: 30,
        lineHeight: 35,
        textAlign: 'center',
        color: '#212121',
        paddingTop: 32
    },
    form: {
        backgroundColor: "#fff",
        height: '60%',
        width: '100%',
        justifyContent: 'flex-start',
        alignItems: 'center'
    },
    inputContainer: {
        marginTop: 33,
        marginHorizontal: 16,
    },
    input: {
        height: 50,
        marginTop: 16,
        borderWidth: 1,
        borderColor: "#E8E8E8",
        borderRadius: 8,
        padding: 16,
        backgroundColor: "#F6F6F6",
        width: 343,

    }
})
