import React, { SetStateAction, useState } from 'react';

import {
    StyleSheet,
    View,
    Text,
    TextInput,
    TouchableWithoutFeedback,
    Keyboard,
    KeyboardAvoidingView,
    Platform,
    TouchableOpacity,
    ImageBackground,
} from 'react-native';

const initialState = {
    email: '',
    password: '',
};
export default function RegistrationScreen() {
    const [isShowKeyboard, setIsShowKeyboard] = useState(false);
    const [isPasswordShown, setIsPasswordShown] = useState(true);

    const passwordToggle = () => {
        setIsPasswordShown((value) => !value);
    };

    const [auth, setAuth] = useState(initialState);
    const passwordHandler = (value: string) => setAuth((prevState) => ({ ...prevState, password: value.toString() }));
    const emailHandler = (value: string) => setAuth((prevState) => ({ ...prevState, email: value.toString() }));

    const keyboardHide = () => {
        setIsShowKeyboard(false);
        Keyboard.dismiss();
    };

    const onHandleSubmit = () => {
        console.log(auth)
        setAuth(initialState)
    };
    const handleSubmit = () => {
        setIsShowKeyboard(false);
        Keyboard.dismiss();
    }
    return (
        <TouchableWithoutFeedback onPress={keyboardHide}>
            <ImageBackground style={styles.image} source={require('../images/bebr.jpg')}>
                <KeyboardAvoidingView
                    behavior={Platform.OS == "ios" ? "padding" : "height"}
                    style={styles.container}
                >
                    <View style={{ ...styles.form, marginBottom: isShowKeyboard ? -94 : 144 }}>
                        <Text style={styles.title}>Войти</Text>
                        <TextInput
                            onSubmitEditing={handleSubmit}
                            value={auth.email}
                            onChangeText={emailHandler}
                            placeholder="Адрес электронной почты"
                            keyboardType="email-address"
                            style={styles.input}
                            textAlign={'left'}
                            onFocus={() => { setIsShowKeyboard(true) }}

                        />
                        <TextInput
                            style={styles.input}
                            onSubmitEditing={handleSubmit}
                            value={auth.password}
                            onChangeText={passwordHandler}
                            placeholder="Пароль"
                            secureTextEntry={isPasswordShown}
                            textAlign={'left'}
                            onFocus={() => { setIsShowKeyboard(true) }}
                        />
                        <TouchableOpacity activeOpacity={0.5} style={styles.passwShow} onPress={passwordToggle}>
                            <Text style={styles.passwShowText}>{isPasswordShown ? 'Показать' : 'Спрятать'}</Text>
                        </TouchableOpacity>
                        <View>
                            <TouchableOpacity activeOpacity={0.8} style={styles.btn} onPress={onHandleSubmit}>
                                <Text style={styles.btnTitle}>Войти</Text>
                            </TouchableOpacity>
                            <TouchableOpacity activeOpacity={0.8} style={{ marginTop: 16 }} onPress={() => { }}>
                                <Text style={styles.link}>Нет аккаунта? Зарегистрироваться</Text>
                            </TouchableOpacity>
                        </View>
                    </View>
                </KeyboardAvoidingView>
            </ImageBackground>
        </TouchableWithoutFeedback>
    );
}

const styles = StyleSheet.create({
    image: {
        flex: 1,
        resizeMode: "cover",
        justifyContent: "flex-end",
    },
    container: {
        backgroundColor: "#fff",
        borderTopLeftRadius: 25,
        borderTopRightRadius: 25,
        paddingHorizontal: 16,
        paddingTop: 32,
    },
    title: {
        fontSize: 30,
        fontWeight: '500',
        lineHeight: 35,
        textAlign: "center",
        letterSpacing: 0.01,
        color: "#212121",
        marginBottom: 20,

    },
    form: {

    },
    passwShowText: {
        fontStyle: 'normal',
        fontWeight: '400',
        fontSize: 16,
        lineHeight: 19,
    },
    passwShow: {
        top: -34,
        marginRight: 0
    },
    input: {
        height: 50,
        marginTop: 16,
        borderWidth: 1,
        borderColor: "#E8E8E8",
        borderRadius: 8,
        padding: 16,
        backgroundColor: "#F6F6F6",
        justifyContent: 'center',
        placeholderTextColor: '#BDBDBD'
    },
    showBtn: {
        position: 'absolute',
        alignSelf: 'flex-end',
        right: 16,
    },
    showTitle: {
        color: '#1B4371',
        fontSize: 16,
        fontWeight: '400',
        lineHeight: 19
    },
    btn: {
        backgroundColor: "#FF6C00",
        borderRadius: 100,
        height: 51,
        alignItems: "center",
        justifyContent: "center",
        marginTop: 43,
    },
    btnTitle: {
        fontSize: 16,
        lineHeight: 19,
        color: "#FFFFFF",
    },
    link: {
        fontSize: 16,
        lineHeight: 19,
        textAlign: "center",
        color: "#1B4371",
    },
    avatar: {
        position: "absolute",
        left: 0,
        right: 0,
        transform: [{ translateY: -150 }],
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
    },
    avatarBg: {
        position: "relative",
        minWidth: 120,
        minHeight: 120,
        backgroundColor: "#F6F6F6",
        borderRadius: 16,
    },
    avatarImg: {
        borderRadius: 16,
    },
    addBtn: {
        position: "absolute",
        bottom: 14,
        right: -13,
        flex: 1,
        justifyContent: "center",
        alignItems: "center",
        width: 25,
        height: 25,
        backgroundColor: "#fff",
        borderRadius: 13,
    },
});
