import React, { useState } from "react";
import {
    StyleSheet,
    Text,
    View,
    ImageBackground,
    TextInput,
    TouchableOpacity,
    Platform,
    KeyboardAvoidingView,
    TouchableWithoutFeedback,
    Keyboard,
} from "react-native";


const initialState = {
    email: "",
    password: "",
};

export default function LoginScreen() {
    const [secureTextEntry, setSecureTextEntry] = useState(true);
    const [textInputPassBtn, setTextInputPassBtn] = useState("Показать");
    const [inputFocus, setInputFocus] = useState(false);
    const [state, setState] = useState(initialState);
    const onFocusInput = () => {
        setInputFocus(true);
    };
    const keyboardHide = () => {
        setInputFocus(false);
        console.log('alo');
        Keyboard.dismiss();
    };
    const onPressBtnPass = () => {
        setSecureTextEntry((prevState) => !prevState);
        setTextInputPassBtn(prevState =>
            prevState === 'Показать' ? 'Спрятать' : "Показать"
        );
    };
    const onSubmitForm = () => {
        console.log(state);
        setState(initialState);
    };
    const onBlurInput = ({ nativeEvent }) => {

    }
    return (
        <TouchableWithoutFeedback onPress={keyboardHide}>
            <ImageBackground
                style={styles.image}
                source={require("../images/bebr.jpg")}
            >
                <View
                    style={{ ...styles.form, paddingBottom: inputFocus ? 32 : 144 }}
                >
                    <Text style={styles.formTitle}>Войти</Text>
                    <View style={styles.inputContainer}>
                        <TextInput
                            onFocus={onFocusInput}
                            onChangeText={(value) =>
                                setState((prevState) => ({ ...prevState, email: value }))
                            }
                            value={state.email}
                            style={styles.input}
                            placeholder="Адрес электронной почты"
                        />
                        <View style={styles.inputPassCont}>
                            <TextInput
                                onFocus={onFocusInput}
                                onBlur={onBlurInput}
                                onChangeText={(value) =>
                                    setState((prevState) => ({
                                        ...prevState,
                                        password: value,
                                    }))
                                }
                                value={state.password}
                                style={{ ...styles.input, marginBottom: 0 }}
                                secureTextEntry={secureTextEntry}
                                placeholder="Пароль"
                            />
                            <TouchableOpacity
                                onPress={onPressBtnPass}
                                activeOpacity={0.7}
                                style={styles.btnPassword}
                            >
                                <Text style={styles.btnPassText}>{textInputPassBtn}</Text>
                            </TouchableOpacity>
                        </View>
                    </View>
                    {!inputFocus && (
                        <TouchableOpacity
                            onPress={onSubmitForm}
                            activeOpacity={0.7}
                            style={styles.btnRegister}
                        >
                            <Text style={styles.btnText}>Увійти</Text>
                        </TouchableOpacity>
                    )}
                    {!inputFocus && (
                        <TouchableOpacity activeOpacity={0.7} style={styles.btnLogin}>
                            <Text style={styles.btnLogin_text}>
                                Немає аккаунта? Зареєструватись
                            </Text>
                        </TouchableOpacity>
                    )}
                </View>
            </ImageBackground>
        </TouchableWithoutFeedback>
    );
}

const styles = StyleSheet.create({
    image: {
        flex: 1,
        resizeMode: "cover",
        justifyContent: "flex-end",
    },
    form: {
        position: "relative",
        borderTopLeftRadius: 30,
        borderTopRightRadius: 30,
        paddingTop: 32,
        paddingHorizontal: 16,
        backgroundColor: "#fff",
        // justifyContent: "center",
        alignItems: "center",
    },

    userImage: {
        position: "absolute",
        top: -60,
        width: 120,
        height: 120,
        borderRadius: 16,
        backgroundColor: "#F6F6F6",
    },
    btnAddUserImage: {
        position: "absolute",
        bottom: 14,
        right: -12,
        borderWidth: 1,
        borderColor: "#FF6C00",
        borderRadius: 50,
        width: 25,
        height: 25,
        alignItems: "center",
        // justifyContent: "flex-start",
    },
    btnAddUserImage_text: {
        fontSize: 18,
        color: "#FF6C00",
    },
    input: {
        fontSize: 16,
        fontWeight: "500",
        borderWidth: 1,
        borderColor: "#E8E8E8",
        height: 50,
        width: "100%",
        borderRadius: 8,
        padding: 16,
        marginBottom: 16,
        backgroundColor: "#F6F6F6",
        color: "#212121",
    },
    formTitle: {
        marginBottom: 33,
        fontSize: 30,
        fontWeight: "500",
        // fontFamily: "Roboto-Bold",
        // fontFamily: "Roboto-Regular",
        // fontFamily: "Sevillana-Regular",
    },
    inputContainer: {
        width: "100%",
    },
    inputPassCont: {
        position: "relative",
    },
    btnPassword: {
        position: "absolute",
        right: 0,
        top: 0,
        padding: 16,
    },
    btnPassText: {
        fontSize: 16,
        color: "#1B4371",
    },

    btnRegister: {
        marginTop: 30,
        marginBottom: 16,
        height: 51,
        width: "100%",
        borderRadius: 100,
        backgroundColor: "#FF6C00",
        alignItems: "center",
        justifyContent: "center",
    },
    btnText: {
        fontSize: 16,
        color: "#FFFFFF",
    },
    btnLogin: {
        // backgroundColor: "orange",
    },
    btnLogin_text: {
        fontSize: 16,
        color: "#1B4371",
    },
});
